import{_ as s,c as a,o as n,a as l}from"./app.228f4434.js";const h=JSON.parse('{"title":"Git Config \u5E38\u7528\u547D\u4EE4","description":"","frontmatter":{"title":"Git Config \u5E38\u7528\u547D\u4EE4","tags":["Git"]},"headers":[{"level":2,"title":"\u8BFB\u53D6","slug":"\u8BFB\u53D6"},{"level":3,"title":"\u67E5\u770B\u914D\u7F6E\u5217\u8868","slug":"\u67E5\u770B\u914D\u7F6E\u5217\u8868"},{"level":3,"title":"\u67E5\u770B\u5177\u4F53\u914D\u7F6E","slug":"\u67E5\u770B\u5177\u4F53\u914D\u7F6E"},{"level":2,"title":"\u5199\u5165","slug":"\u5199\u5165"},{"level":3,"title":"\u4EE5\u914D\u7F6E\u7528\u6237\u540D\u548C\u90AE\u7BB1\u4E3A\u4F8B","slug":"\u4EE5\u914D\u7F6E\u7528\u6237\u540D\u548C\u90AE\u7BB1\u4E3A\u4F8B"},{"level":2,"title":"\u7F16\u8F91","slug":"\u7F16\u8F91"},{"level":2,"title":"\u5220\u9664","slug":"\u5220\u9664"},{"level":2,"title":"\u5E38\u7528\u914D\u7F6E","slug":"\u5E38\u7528\u914D\u7F6E"}],"relativePath":"blog/20220423-git-config.md"}'),e={name:"blog/20220423-git-config.md"},p=l(`<h1 id="git-config-\u5E38\u7528\u547D\u4EE4" tabindex="-1">Git Config \u5E38\u7528\u547D\u4EE4 <a class="header-anchor" href="#git-config-\u5E38\u7528\u547D\u4EE4" aria-hidden="true">#</a></h1><h2 id="\u8BFB\u53D6" tabindex="-1">\u8BFB\u53D6 <a class="header-anchor" href="#\u8BFB\u53D6" aria-hidden="true">#</a></h2><p>\u7528 <code>git config</code> \u8BFB\u53D6\u914D\u7F6E\u65F6\uFF0C\u9ED8\u8BA4\u60C5\u51B5\u4E0B\u4ECE\u7CFB\u7EDF\u3001\u5168\u5C40\u548C\u5B58\u50A8\u5E93\u672C\u5730\u914D\u7F6E\u6587\u4EF6\u4E2D\u8BFB\u53D6\u8FD9\u4E9B\u503C\uFF0C\u8BFB\u53D6\u4F18\u5148\u7EA7\u4E3A\uFF1A\u5B58\u50A8\u5E93\u672C\u5730 &gt; \u5168\u5C40 &gt; \u7CFB\u7EDF\u3002\u53EF\u4EE5\u8FFD\u52A0 <code>--system</code>\uFF0C<code>--global</code>\uFF0C<code>--local</code> \u6216 <code>--file &lt;filename&gt;</code> \u660E\u786E\u8BFB\u53D6\u4F4D\u7F6E\u3002</p><h3 id="\u67E5\u770B\u914D\u7F6E\u5217\u8868" tabindex="-1">\u67E5\u770B\u914D\u7F6E\u5217\u8868 <a class="header-anchor" href="#\u67E5\u770B\u914D\u7F6E\u5217\u8868" aria-hidden="true">#</a></h3><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#7B7F8B;"># \u4F4D\u7F6E\u53C2\u6570\u53EF\u9009\uFF0C\u9ED8\u8BA4\u4E3A --local\uFF0C\u53EF\u9009\u503C --system\uFF0C--global\uFF0C--local \u6216 --file &lt;filename&gt;</span></span>
<span class="line"><span style="color:#7B7F8B;"># Git \u4F1A\u5217\u51FA\u7CFB\u7EDF\u3001\u5168\u5C40\u548C\u5B58\u50A8\u5E93\u672C\u5730\u914D\u7F6E\u6587\u4EF6\u4E2D\u6240\u6709\u7684\u914D\u7F6E\uFF0C\u91CD\u590D\u914D\u7F6E\u6309\u7167\u300C\u7CFB\u7EDF\u3001\u5168\u5C40\u3001\u5B58\u50A8\u5E93\u672C\u5730\u300D\u7684\u987A\u5E8F\uFF0CGit \u4F1A\u4F7F\u7528\u6700\u540E\u4E00\u4E2A\u503C</span></span>
<span class="line"><span style="color:#F6F6F4;">git config [\u4F4D\u7F6E\u53C2\u6570] --list</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7B7F8B;"># \u4F8B\u5982</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global --list</span></span>
<span class="line"></span></code></pre></div><h3 id="\u67E5\u770B\u5177\u4F53\u914D\u7F6E" tabindex="-1">\u67E5\u770B\u5177\u4F53\u914D\u7F6E <a class="header-anchor" href="#\u67E5\u770B\u5177\u4F53\u914D\u7F6E" aria-hidden="true">#</a></h3><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#F6F6F4;">git config [\u4F4D\u7F6E\u53C2\u6570] </span><span style="color:#F286C4;">&lt;</span><span style="color:#F6F6F4;">\u53C2\u6570\u540D</span><span style="color:#F286C4;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7B7F8B;"># \u4F8B\u5982</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global user.name</span></span>
<span class="line"></span></code></pre></div><h2 id="\u5199\u5165" tabindex="-1">\u5199\u5165 <a class="header-anchor" href="#\u5199\u5165" aria-hidden="true">#</a></h2><p>\u7528 <code>git config</code> \u5199\u5165\u914D\u7F6E\u65F6\uFF0C\u9ED8\u8BA4\u60C5\u51B5\u4E0B\u4F1A\u5C06\u65B0\u503C\u5199\u5165<strong>\u5B58\u50A8\u5E93\u672C\u5730</strong>\u7684\u914D\u7F6E\u6587\u4EF6\uFF0C\u53EF\u4EE5\u8FFD\u52A0 <code>--system</code>\uFF0C<code>--global</code>\uFF0C<code>--local</code> \u6216 <code>--file &lt;filename&gt;</code> \u660E\u786E\u5199\u5165\u4F4D\u7F6E\uFF0C<code>--local</code> \u547D\u4EE4\u5373\u4E3A\u9ED8\u8BA4\u503C\u3002</p><p>\u6709\u4E24\u79CD\u5199\u5165\u65B9\u5F0F\uFF1A</p><ul><li>\u7528 <code>--add</code> \u547D\u4EE4\uFF1A\u6709\u91CD\u590D\u7684\u914D\u7F6E\uFF0C\u4E0D\u4F1A\u8986\u76D6\uFF0C\u4F1A\u8FFD\u52A0\u5728\u914D\u7F6E\u6587\u4EF6\u6700\u540E\u9762\uFF0CGit \u4F1A\u4EE5\u6700\u540E\u9762\u4E00\u6761\u4E3A\u51C6</li><li>\u4E0D\u7528 <code>--add</code> \u547D\u4EE4\uFF1A\u6709\u91CD\u590D\u7684\u914D\u7F6E\uFF0C\u76F4\u63A5\u8986\u76D6</li></ul><h3 id="\u4EE5\u914D\u7F6E\u7528\u6237\u540D\u548C\u90AE\u7BB1\u4E3A\u4F8B" tabindex="-1">\u4EE5\u914D\u7F6E\u7528\u6237\u540D\u548C\u90AE\u7BB1\u4E3A\u4F8B <a class="header-anchor" href="#\u4EE5\u914D\u7F6E\u7528\u6237\u540D\u548C\u90AE\u7BB1\u4E3A\u4F8B" aria-hidden="true">#</a></h3><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#7B7F8B;"># \u914D\u7F6E\u5168\u5C40\u7528\u6237\u540D</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global user.name </span><span style="color:#DEE492;">&quot;</span><span style="color:#E7EE98;">your name</span><span style="color:#DEE492;">&quot;</span></span>
<span class="line"><span style="color:#7B7F8B;"># \u914D\u7F6E\u5168\u5C40\u90AE\u7BB1</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global user.email </span><span style="color:#DEE492;">&quot;</span><span style="color:#E7EE98;">your_email@example.com</span><span style="color:#DEE492;">&quot;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7B7F8B;"># \u5982\u679C\u5E0C\u671B\u5728\u7279\u5B9A\u7684\u9879\u76EE\u4E2D\u4F7F\u7528\u7279\u5B9A\u7684\u540D\u79F0\u548C\u90AE\u7BB1\u5730\u5740</span></span>
<span class="line"><span style="color:#97E1F1;">cd</span><span style="color:#F6F6F4;"> </span><span style="color:#F286C4;">&lt;</span><span style="color:#F6F6F4;">\u4F60\u7684\u9879\u76EE\u6839\u76EE\u5F55</span><span style="color:#F286C4;">&gt;</span></span>
<span class="line"><span style="color:#F6F6F4;">git config user.name </span><span style="color:#DEE492;">&quot;</span><span style="color:#E7EE98;">your name</span><span style="color:#DEE492;">&quot;</span></span>
<span class="line"><span style="color:#7B7F8B;"># \u914D\u7F6E\u5168\u5C40\u90AE\u7BB1</span></span>
<span class="line"><span style="color:#F6F6F4;">git config user.email </span><span style="color:#DEE492;">&quot;</span><span style="color:#E7EE98;">your_email@example.com</span><span style="color:#DEE492;">&quot;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7B7F8B;"># \u901A\u8FC7 --add \u547D\u4EE4\u5199\u5165\u914D\u7F6E</span></span>
<span class="line"><span style="color:#7B7F8B;"># \u5982\u679C\u5DF2\u7ECF\u6709\u4E86\u76F8\u540C\u7684\u914D\u7F6E\uFF0C\u4E0D\u4F1A\u8986\u76D6\uFF0C\u4F1A\u5728\u914D\u7F6E\u6587\u4EF6\u6700\u540E\u9762\u63D2\u5165\u4E00\u6761</span></span>
<span class="line"><span style="color:#7B7F8B;"># Git \u4F1A\u4EE5\u6700\u540E\u9762\u4E00\u6761\u4E3A\u51C6</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global --add user.name </span><span style="color:#DEE492;">&quot;</span><span style="color:#E7EE98;">your name</span><span style="color:#DEE492;">&quot;</span></span>
<span class="line"></span></code></pre></div><h2 id="\u7F16\u8F91" tabindex="-1">\u7F16\u8F91 <a class="header-anchor" href="#\u7F16\u8F91" aria-hidden="true">#</a></h2><p><code>--edit</code> \u547D\u540D\u4F1A</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#F6F6F4;">git config [\u4F4D\u7F6E\u53C2\u6570] --edit</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7B7F8B;"># \u4F8B\u5982</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global --edit</span></span>
<span class="line"><span style="color:#7B7F8B;"># \u7B49\u540C\u4E8E</span></span>
<span class="line"><span style="color:#F6F6F4;">vi </span><span style="color:#F286C4;">~</span><span style="color:#F6F6F4;">/.gitconfig</span></span>
<span class="line"></span></code></pre></div><h2 id="\u5220\u9664" tabindex="-1">\u5220\u9664 <a class="header-anchor" href="#\u5220\u9664" aria-hidden="true">#</a></h2><p>\u53EF\u4EE5\u901A\u8FC7 <code>--unset</code> \u547D\u4EE4\u5220\u9664\u914D\u7F6E</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#7B7F8B;"># \u5982\u679C\u6CA1\u6709\u91CD\u590D\u7684\u914D\u7F6E\uFF0C\u76F4\u63A5\u5220\u9664</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global --unset user.name</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7B7F8B;"># \u5982\u679C\u6709\u91CD\u590D\u7684\u914D\u7F6E\uFF0C\u9700\u8981\u6307\u5B9A\u914D\u7F6E\u8BE6\u60C5\uFF0C\u4E0D\u7136\u4F1A\u63D0\u793A warning: user.name has multiple values</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global --unset user.name </span><span style="color:#DEE492;">&quot;</span><span style="color:#E7EE98;">your name</span><span style="color:#DEE492;">&quot;</span></span>
<span class="line"></span></code></pre></div><h2 id="\u5E38\u7528\u914D\u7F6E" tabindex="-1">\u5E38\u7528\u914D\u7F6E <a class="header-anchor" href="#\u5E38\u7528\u914D\u7F6E" aria-hidden="true">#</a></h2><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#7B7F8B;"># \u8BBE\u7F6E\u9ED8\u8BA4\u5206\u652F\u4E3A main</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global init.defaultBranch main</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7B7F8B;"># \u5728\u5168\u5C40\u4FDD\u5B58\u5F53\u524D\u4ED3\u5E93\u7684\u8D26\u53F7\u5BC6\u7801\uFF0C\u6B64\u65F6\u9700\u8981\u514B\u9686\u65B9\u5F0F\u662F http</span></span>
<span class="line"><span style="color:#F6F6F4;">git config --global credential.helper store</span></span>
<span class="line"><span style="color:#7B7F8B;"># \u518D\u8F93\u5165\u4E00\u6B21\u5BC6\u7801</span></span>
<span class="line"><span style="color:#F6F6F4;">git pull</span></span>
<span class="line"></span></code></pre></div>`,21),o=[p];function c(t,i,r,d,g,F){return n(),a("div",null,o)}var u=s(e,[["render",c]]);export{h as __pageData,u as default};
